import React from "react";
import { useNavigate } from "react-router-dom";

const Explore = ({ courses }) => {
  const navigate = useNavigate();

  return (
    <div className="p-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
      {courses.map((course) => (
        <div
          key={course._id}
          className="bg-white shadow-lg rounded-lg p-4 hover:shadow-xl transition duration-300 cursor-pointer"
          onClick={() => navigate(`/course/${course._id}`)}
        >
          <h3 className="text-xl font-bold text-gray-800">{course.name}</h3>
          <p className="text-gray-600">{course.description}</p>
          <p className="text-sm text-gray-500 mt-2">Time Credit Value: {course.timeCreditValue}</p>
          <p className="text-sm text-gray-500">Available Hours: {course.availableHours}</p>
        </div>
      ))}
    </div>
  );
};

export default Explore;
