import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); 
  const [isAdminLogin, setIsAdminLogin] = useState(false);
  const navigate = useNavigate();

  // Initialize Toastify
  const showToast = (message, type) => {
    toast[type](message, {
      position: "top-right",
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      theme: "colored",
    });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", { email, password });

      localStorage.setItem("token", res.data.token);
      
      // Check if this is an admin login attempt
      if (isAdminLogin) {
        // Check if user is admin
        const profileRes = await axios.get("http://localhost:5000/api/auth/profile", {
          headers: { Authorization: `Bearer ${res.data.token}` },
        });
        
        if (profileRes.data.isAdmin) {
          localStorage.setItem("adminToken", res.data.token);
          showToast("üîê Admin login successful!", "success");
          setTimeout(() => navigate("/admin/dashboard"), 2000);
        } else {
          showToast("‚ö†Ô∏è Access denied. Admin privileges required.", "error");
        }
      } else {
        showToast("üéâ Welcome Back! You are successfully logged in.", "success");
        setTimeout(() => navigate("/dashboard"), 2000);
      }
    } catch (error) {
      showToast("‚ö†Ô∏è Invalid Credentials! Please try again.", "error");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-500 to-purple-600">
      <div className="bg-white p-8 shadow-lg rounded-2xl w-96">
        <h2 className="text-3xl font-bold text-center text-blue-600 mb-6">
          {isAdminLogin ? "Admin Login" : "Login"}
        </h2>

        <form onSubmit={handleLogin} className="flex flex-col">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="p-3 mb-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="p-3 mb-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
          />
          <button
            type="submit"
            className={`${isAdminLogin 
              ? "bg-indigo-700 hover:bg-indigo-800" 
              : "bg-blue-600 hover:bg-blue-700"} 
              text-white py-2 rounded-md font-semibold transition`}
          >
            {isAdminLogin ? "Login as Admin" : "Login"}
          </button>
        </form>

        <div className="text-center mt-4">
          <button
            onClick={() => setIsAdminLogin(!isAdminLogin)}
            className="text-purple-600 font-semibold hover:underline"
          >
            {isAdminLogin ? "Switch to User Login" : "Login as Administrator"}
          </button>
        </div>

        <p className="text-center text-gray-600 mt-4">
          Don't have an account?{" "}
          <a href="/signup" className="text-purple-600 font-semibold hover:underline">
            Sign up
          </a>
        </p>
      </div>
    </div>
  );
}

export default Login;
