import React from "react";

const CourseCard = ({ course }) => {
  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden transform hover:scale-105 transition-all duration-300">
      {course.thumbnail ? (
        <img
          src={course.thumbnail}
          alt={course.name}
          className="w-full h-48 object-cover"
        />
      ) : (
        <div className="w-full h-48 bg-gray-200 flex items-center justify-center text-gray-500">
          No Image
        </div>
      )}
      <div className="p-5 space-y-3">
        <h4 className="text-xl font-semibold text-gray-800">{course.name}</h4>
        <p className="text-gray-600 text-sm">{course.description}</p>
        <p className="text-sm text-gray-500">
          Time Credit Value: {course.timeCreditValue}
        </p>
        <p className="text-sm text-gray-500">
          Available Hours: {course.availableHours}
        </p>
      </div>
    </div>
  );
};

export default CourseCard;
