import React from "react";
import { useNavigate } from "react-router-dom";
import {
  FiLogIn,
  FiUserPlus,
  FiHome,
  FiLayers,
  FiLogOut,
  FiCompass,
} from "react-icons/fi";

const Sidebar = ({ isLoggedIn, handleLogout, showSidebar, setShowSidebar }) => {
  const navigate = useNavigate();

  return (
    <div
      className={`bg-white shadow-lg p-5 h-full transition-all duration-300 ${
        showSidebar ? "w-64" : "w-0"
      }`}
    >
      {showSidebar && (
        <div className="flex flex-col h-full">
          <h2 className="text-2xl font-bold text-purple-600 mb-6 flex items-center">
            <FiLayers className="mr-2" /> Virtual Time Bank
          </h2>
          <nav className="flex flex-col space-y-4">
            <button
              onClick={() => navigate("/")}
              className="flex items-center p-3 rounded-lg hover:bg-gray-100 transition"
            >
              <FiHome className="mr-3" /> Home
            </button>
            {!isLoggedIn ? (
              <>
                <button
                  onClick={() => navigate("/login")}
                  className="flex items-center p-3 rounded-lg hover:bg-gray-100 transition"
                >
                  <FiLogIn className="mr-3" /> Login
                </button>
                <button
                  onClick={() => navigate("/signup")}
                  className="flex items-center p-3 rounded-lg hover:bg-gray-100 transition"
                >
                  <FiUserPlus className="mr-3" /> Sign Up
                </button>
              </>
            ) : (
              <>
                <button
                  onClick={() => navigate("/dashboard")}
                  className="flex items-center p-3 rounded-lg hover:bg-gray-100 transition"
                >
                  <FiLayers className="mr-3" /> Dashboard
                </button>
                <button
                  onClick={() => navigate("/explore")}
                  className="flex items-center p-3 rounded-lg hover:bg-blue-100"
                >
                  <FiCompass className="mr-3" /> Explore
                </button>
                <button
                  onClick={() => navigate("/transactions")}
                  className="flex items-center p-3 rounded-lg hover:bg-purple-100 transition"
                >
                  ðŸ“œ Transaction History
                </button>
                <button
                  onClick={handleLogout}
                  className="flex items-center p-3 text-red-600 rounded-lg hover:bg-red-100 transition"
                >
                  <FiLogOut className="mr-3" /> Logout
                </button>
              </>
            )}
          </nav>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
