import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";
import "react-toastify/dist/ReactToastify.css";

const CourseDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [purchasing, setPurchasing] = useState(false);
  const [showSidebar, setShowSidebar] = useState(true);

  useEffect(() => {
    const fetchCourseDetails = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch(`http://localhost:5000/api/courses/${id}`, {
          method: "GET",
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.message || "Failed to fetch course details.");
        }

        const data = await response.json();
        setCourse(data);
      } catch (error) {
        console.error("Error fetching course details:", error);
        toast.error(error.message || "An error occurred while fetching course details.");
        navigate("/explore");
      } finally {
        setLoading(false);
      }
    };

    fetchCourseDetails();
  }, [id, navigate]);

  const handlePurchase = async () => {
    setPurchasing(true);
    try {
      const token = localStorage.getItem("token");
      
      // First, create a transaction record
      const transactionResponse = await fetch(`http://localhost:5000/api/transactions/spend`, {
        method: "POST",
        headers: { 
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ skillId: id })
      });

      if (!transactionResponse.ok) {
        const errorData = await transactionResponse.json();
        throw new Error(errorData.message || "Failed to process transaction");
      }

      // After successful transaction, process the course purchase
      const purchaseResponse = await fetch(`http://localhost:5000/api/courses/${id}/purchase`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!purchaseResponse.ok) {
        const errorData = await purchaseResponse.json();
        throw new Error(errorData.message || "Failed to purchase course.");
      }

      const purchaseData = await purchaseResponse.json();
      toast.success(purchaseData.message || "Course purchased successfully!");
      
      // Redirect to transaction history so user can see their purchase
      setTimeout(() => navigate("/transactions"), 1500);
    } catch (error) {
      console.error("Error purchasing course:", error);
      toast.error(error.message || "An error occurred while purchasing the course.");
    } finally {
      setPurchasing(false);
    }
  };

  if (loading) {
    return (
      <div className="flex h-screen items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500"></div>
      </div>
    );
  }

  if (!course) {
    return <div className="p-6 text-center text-red-500">Course not found.</div>;
  }

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar
        isLoggedIn={true}
        handleLogout={() => {
          localStorage.removeItem("token");
          navigate("/login");
        }}
        showSidebar={showSidebar}
        setShowSidebar={setShowSidebar}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <Navbar
          isLoggedIn={true}
          showSidebar={showSidebar}
          setShowSidebar={setShowSidebar}
        />

        {/* Course Details Section */}
        <div className="p-6 max-w-4xl mx-auto bg-white shadow-lg rounded-lg mt-6">
          <h2 className="text-3xl font-bold text-purple-700 mb-4">{course.name}</h2>
          <p className="text-gray-600 mb-4">{course.description}</p>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4">
            <p className="text-gray-600">
              <span className="font-semibold">Time Credit Value:</span> {course.timeCreditValue}
            </p>
            <p className="text-gray-600">
              <span className="font-semibold">Available Hours:</span> {course.availableHours}
            </p>
            <p className="text-gray-600 col-span-2">
              <span className="font-semibold">Google Meet Link:</span>{" "}
              <a
                href={course.googleMeetLink}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 underline"
              >
                {course.googleMeetLink}
              </a>
            </p>
            <p className="text-gray-600 col-span-2">
              <span className="font-semibold">Created By:</span> {course.user?.name || "Unknown"}
            </p>
          </div>
          <button
            onClick={handlePurchase}
            disabled={purchasing}
            className={`${
              purchasing
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-purple-600 hover:bg-purple-700"
            } text-white px-6 py-3 rounded-lg transition`}
          >
            {purchasing ? "Processing..." : "Purchase Course"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default CourseDetails;
