import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";
import "react-toastify/dist/ReactToastify.css";

const CourseDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showSidebar, setShowSidebar] = useState(true);

  useEffect(() => {
    const fetchCourseDetails = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch(`http://localhost:5000/api/courses/${id}`, {
          method: "GET",
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.message || "Failed to fetch course details.");
        }

        const data = await response.json();
        setCourse(data);
      } catch (error) {
        console.error("Error fetching course details:", error);
        toast.error(error.message || "An error occurred while fetching course details.");
        navigate("/explore");
      } finally {
        setLoading(false);
      }
    };

    fetchCourseDetails();
  }, [id, navigate]);

  const handlePurchase = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(`http://localhost:5000/api/courses/${id}/purchase`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to purchase course.");
      }

      const data = await response.json();
      toast.success(data.message || "Course purchased successfully!");
      navigate("/explore");
    } catch (error) {
      console.error("Error purchasing course:", error);
      toast.error(error.message || "An error occurred while purchasing the course.");
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!course) {
    return <p>Course not found.</p>;
  }

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar
        isLoggedIn={true}
        handleLogout={() => {
          localStorage.removeItem("token");
          navigate("/login");
        }}
        showSidebar={showSidebar}
        setShowSidebar={setShowSidebar}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <Navbar
          isLoggedIn={true}
          showSidebar={showSidebar}
          setShowSidebar={setShowSidebar}
        />

        {/* Course Details Section */}
        <div className="p-6 max-w-4xl mx-auto bg-white shadow-lg rounded-lg mt-6">
          <h2 className="text-3xl font-bold text-purple-700 mb-4">{course.name}</h2>
          <p className="text-gray-600 mb-4">{course.description}</p>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4">
            <p className="text-gray-600">
              <span className="font-semibold">Time Credit Value:</span> {course.timeCreditValue}
            </p>
            <p className="text-gray-600">
              <span className="font-semibold">Available Hours:</span> {course.availableHours}
            </p>
            <p className="text-gray-600 col-span-2">
              <span className="font-semibold">Google Meet Link:</span>{" "}
              <a
                href={course.googleMeetLink}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 underline"
              >
                {course.googleMeetLink}
              </a>
            </p>
            <p className="text-gray-600 col-span-2">
              <span className="font-semibold">Created By:</span> {course.user?.name || "Unknown"}
            </p>
          </div>
          <button
            onClick={handlePurchase}
            className="bg-purple-600 text-white px-6 py-3 rounded-lg hover:bg-purple-700 transition"
          >
            Purchase Course
          </button>
        </div>
      </div>
    </div>
  );
};

export default CourseDetails;
