import React, { useState } from "react";
import axios from "axios";

function AddSkill() {
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [hoursAvailable, setHoursAvailable] = useState("");

    const handleAddSkill = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem("token");
        if (!token) {
            alert("You must be logged in to add a skill.");
            return;
        }
        try {
            const response = await axios.post(
                "http://localhost:5000/api/skills/add",
                { name, description, hoursAvailable },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert("Skill added successfully!");
            setName("");
            setDescription("");
            setHoursAvailable("");
        } catch (error) {
            alert("Failed to add skill: " + error.response?.data?.message);
        }
    };

    return (
        <div>
            <h2>Add a Skill</h2>
            <form onSubmit={handleAddSkill}>
                <input type="text" placeholder="Skill Name" value={name} onChange={(e) => setName(e.target.value)} required />
                <input type="text" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} required />
                <input type="number" placeholder="Available Hours" value={hoursAvailable} onChange={(e) => setHoursAvailable(e.target.value)} required />
                <button type="submit">Add Skill</button>
            </form>
        </div>
    );
}

export default AddSkill;
