import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";

function Home() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [skills, setSkills] = useState([]);
  const [showSidebar, setShowSidebar] = useState(true);
  const navigate = useNavigate();
  const skillsRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
      checkAdminStatus(token);
    }
    fetchSkills();
  }, []);

  const checkAdminStatus = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setIsAdmin(data.isAdmin || false);
    } catch (error) {
      console.error("Error checking admin status:", error);
    }
  };

  const fetchSkills = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/skills");
      const data = await response.json();
      setSkills(data);
    } catch (error) {
      console.error("Error fetching skills:", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("adminToken");
    setIsLoggedIn(false);
    setIsAdmin(false);
    navigate("/login");
  };

  const scrollToSkills = () => {
    if (skillsRef.current) {
      skillsRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <div className="h-screen flex">
      {/* Sidebar */}
      <Sidebar
        isLoggedIn={isLoggedIn}
        handleLogout={handleLogout}
        showSidebar={showSidebar}
        setShowSidebar={setShowSidebar}
        isAdmin={isAdmin}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-auto">
        {/* Navbar */}
        <Navbar
          isLoggedIn={isLoggedIn}
          showSidebar={showSidebar}
          setShowSidebar={setShowSidebar}
          isAdmin={isAdmin}
        />

        {/* Hero Section */}
        <div className="relative bg-gradient-to-br from-purple-500 to-indigo-600 text-white h-[400px] flex items-center justify-center pt-20 px-4">
          <div className="text-center max-w-2xl">
            <h2 className="text-5xl font-extrabold leading-tight mb-4">
              Welcome to{" "}
              <span className="text-yellow-300">Virtual Time Bank</span>
            </h2>
            <p className="text-lg font-light mb-6">
              Exchange your skills and earn time credits! Join our community and
              start saving time today.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <button
                onClick={scrollToSkills}
                className="px-6 py-3 bg-yellow-400 text-purple-800 font-semibold rounded-full shadow-md hover:bg-yellow-300 transition"
              >
                Get Started
              </button>
              
              {isAdmin && (
                <button
                  onClick={() => navigate("/admin/dashboard")}
                  className="px-6 py-3 bg-indigo-800 text-white font-semibold rounded-full shadow-md hover:bg-indigo-900 transition"
                >
                  Admin Dashboard
                </button>
              )}
            </div>
          </div>
        </div>

        {/* Available Skills Section */}
        <div ref={skillsRef} className="p-6">
          <h3 className="text-3xl font-semibold text-purple-600 mb-6 text-center">
            Available Skills
          </h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {skills.length > 0 ? (
              skills.map((skill) => (
                <div
                  key={skill._id}
                  className="bg-white shadow-lg rounded-xl overflow-hidden transform hover:scale-105 transition-all duration-300 cursor-pointer"
                  onClick={() => navigate(`/skill/${skill._id}`)}
                >
                  <div className="h-48 bg-gradient-to-r from-purple-500 to-indigo-600 flex items-center justify-center text-white text-3xl font-bold">
                    {skill.name?.charAt(0) || "?"}
                  </div>
                  <div className="p-5 space-y-3">
                    <h4 className="text-xl font-semibold text-gray-800">
                      {skill.name}
                    </h4>
                    <p className="text-gray-600 text-sm">
                      {skill.timeCreditValue} Credits
                    </p>
                    <p className="text-sm text-gray-500">
                      Created by:{" "}
                      <span className="font-medium text-gray-800">
                        {skill.user?.name || "Unknown"}
                      </span>
                    </p>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-500 text-center w-full">
                No skills available yet.
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
