import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import AddSkill from "./AddSkill";

function Dashboard() {
    const [user, setUser] = useState(null);
    const [skills, setSkills] = useState([]);
    const [search, setSearch] = useState("");
    const [error, setError] = useState(null);
    const navigate = useNavigate();


    useEffect(() => {
        console.log("Dashboard Loaded");
        const token = localStorage.getItem("token");
        if (!token) {
            console.log("No token found. Redirecting to login.");
            window.location.href = "/login";
        } else {
            fetchUserProfile(token);
            fetchSkills();
        }
    }, []);
    





    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/login"); // Redirect if not logged in
        } else {
            fetchUserProfile(token);
            fetchSkills();
        }
    }, []);

    const fetchUserProfile = async (token) => {
        try {
            const response = await fetch("http://localhost:5000/api/auth/profile", {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`,  // ✅ Add "Bearer " prefix
                    "Content-Type": "application/json"
                },
            });
    
            if (!response.ok) {
                throw new Error("Failed to fetch user profile");
            }
    
            const data = await response.json();
            setUser(data);
        } catch (error) {
            console.error("Error fetching user profile:", error);
            alert("Session expired! Please log in again.");
            localStorage.removeItem("token");
            window.location.href = "/login";
        }
    };
    
    const fetchSkills = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/skills");
            const data = await response.json();
            if (response.ok) {
                setSkills(data);
            } else {
                setError("Failed to load skills.");
            }
        } catch (error) {
            console.error("Error fetching skills:", error);
            setError("Could not fetch skills.");
        }
    };

    return (
        <div>
            <h2>Dashboard</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}

            {user ? (
                <div>
                    <h3>Welcome, {user.name}!</h3>
                    <p>Your Email: {user.email}</p>
                    <p>Your Time Credits: {user.credits || 0} hours</p>

                    {/* Add Skill Button */}
                    <button onClick={() => navigate("/add-skill")}>Add Skill</button>

                    {/* Search Bar */}
                    <input 
                        type="text" 
                        placeholder="Search skills..." 
                        value={search} 
                        onChange={(e) => setSearch(e.target.value)} 
                    />

                    {/* Skill Listings */}
                    <h3>Available Skills</h3>
                    <ul>
                        {skills
                            .filter(skill => skill.name.toLowerCase().includes(search.toLowerCase()))
                            .map(skill => (
                                <li key={skill._id}>
                                    {skill.name} - {skill.user?.name || "Unknown"} ({skill.hours} hours)
                                </li>
                            ))}
                    </ul>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
}

export default Dashboard;
