import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const CourseDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCourseDetails = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch(`http://localhost:5000/api/courses/${id}`, {
          method: "GET",
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.message || "Failed to fetch course details.");
        }

        const data = await response.json();
        setCourse(data);
      } catch (error) {
        console.error("Error fetching course details:", error);
        toast.error(error.message || "An error occurred while fetching course details.");
        navigate("/explore");
      } finally {
        setLoading(false);
      }
    };

    fetchCourseDetails();
  }, [id, navigate]);

  const handlePurchase = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(`http://localhost:5000/api/courses/${id}/purchase`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to purchase course.");
      }

      const data = await response.json();
      toast.success(data.message || "Course purchased successfully!");
      navigate("/explore");
    } catch (error) {
      console.error("Error purchasing course:", error);
      toast.error(error.message || "An error occurred while purchasing the course.");
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!course) {
    return <p>Course not found.</p>;
  }

  return (
    <div className="p-6 max-w-3xl mx-auto bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold text-purple-700 mb-4">{course.name}</h2>
      <p className="text-gray-600 mb-2">{course.description}</p>
      <p className="text-gray-600 mb-2">Time Credit Value: {course.timeCreditValue}</p>
      <p className="text-gray-600 mb-2">Available Hours: {course.availableHours}</p>
      <p className="text-gray-600 mb-4">Google Meet Link: <a href={course.googleMeetLink} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">{course.googleMeetLink}</a></p>
      <p className="text-gray-600 mb-4">Created By: {course.user?.name || "Unknown"}</p>
      <button
        onClick={handlePurchase}
        className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 transition"
      >
        Purchase Course
      </button>
    </div>
  );
};

export default CourseDetails;
