import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const CourseDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [course, setCourse] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCourseDetails = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await fetch(`http://localhost:5000/api/courses/${id}`, {
                    method: "GET",
                    headers: { Authorization: `Bearer ${token}` },
                });
                const data = await response.json();
                if (response.ok) {
                    setCourse(data);
                } else {
                    alert(data.message || "Failed to fetch course details.");
                    navigate("/explore");
                }
            } catch (error) {
                console.error("Error fetching course details:", error);
                alert("An error occurred while fetching course details.");
                navigate("/explore");
            } finally {
                setLoading(false);
            }
        };

        fetchCourseDetails();
    }, [id, navigate]);

    const handlePurchase = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await fetch(`http://localhost:5000/api/courses/${id}/purchase`, {
                method: "POST",
                headers: { Authorization: `Bearer ${token}` },
            });
            const data = await response.json();
            if (response.ok) {
                alert(data.message);
                navigate("/explore");
            } else {
                alert(data.message || "Failed to purchase course.");
            }
        } catch (error) {
            console.error("Error purchasing course:", error);
            alert("An error occurred while purchasing the course.");
        }
    };

    if (loading) {
        return <p>Loading...</p>;
    }

    if (!course) {
        return <p>Course not found.</p>;
    }

    return (
        <div className="p-6">
            <h2 className="text-2xl font-bold text-purple-700 mb-4">{course.name}</h2>
            <p className="text-gray-600 mb-2">{course.description}</p>
            <p className="text-gray-600 mb-2">Time Credit Value: {course.timeCreditValue}</p>
            <p className="text-gray-600 mb-4">Owner: {course.owner?.name || "Unknown"}</p>
            <button
                onClick={handlePurchase}
                className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
            >
                Get Course
            </button>
        </div>
    );
};

export default CourseDetails;
