import React, { useEffect, useState } from "react";
import { getTransactionHistory } from "../services/transactionService";

const TransactionHistory = () => {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const data = await getTransactionHistory();
        setTransactions(data);
      } catch (error) {
        console.error("Error fetching transaction history:", error);
      }
    };
    fetchHistory();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold text-purple-700 mb-4">Transaction History</h2>
      {transactions.length > 0 ? (
        <ul className="space-y-4">
          {transactions.map((transaction) => (
            <li key={transaction._id} className="bg-white p-4 rounded-lg shadow-md">
              <p>
                <strong>{transaction.type === "earn" ? "Earned" : "Spent"}:</strong>{" "}
                {transaction.amount} credits
              </p>
              <p>
                <strong>Skill:</strong> {transaction.skill?.name || "Unknown"}
              </p>
              <p className="text-gray-500 text-sm">
                {new Date(transaction.createdAt).toLocaleString()}
              </p>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-gray-500">No transaction history available.</p>
      )}
    </div>
  );
};

export default TransactionHistory;
