import React, { useEffect, useState } from "react";

const Explore = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/courses");
        if (!response.ok) throw new Error("Failed to fetch courses");
        const data = await response.json();
        setCourses(data);
      } catch (error) {
        console.error("Error fetching courses:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold text-purple-700 mb-6">Explore Courses</h2>
      
      {loading ? (
        <p>Loading courses...</p>
      ) : courses.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {courses.map((course) => (
            <div key={course._id} className="bg-white rounded-lg shadow-md overflow-hidden hover:scale-105 transition-all">
              <img
                src={course.image}
                alt={course.title}
                className="w-full h-40 object-cover"
              />
              <div className="p-4">
                <h3 className="text-xl font-semibold">{course.title}</h3>
                <p className="text-gray-500">{course.description}</p>
                <p className="text-gray-700 font-medium">
                  Date: {new Date(course.date).toLocaleDateString()} | Time: {course.time}
                </p>
                <a
                  href={course.meetLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:underline"
                >
                  Join Meet
                </a>
                <p className="text-gray-500">
                  Created by: {course.createdBy?.name || "Unknown"}
                </p>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No courses available yet.</p>
      )}
    </div>
  );
};

export default Explore;
