import { earnCredits, spendCredits } from "../services/transactionService";

const SkillCard = ({ skill }) => {
  const handleEarn = async () => {
    try {
      await earnCredits(skill._id);
      alert("Credits Earned!");
    } catch (error) {
      alert(`Error earning credits: ${error.response.data.message}`);
    }
  };

  const handleSpend = async () => {
    try {
      await spendCredits(skill._id);
      alert("Credits Spent!");
    } catch (error) {
      alert(`Error spending credits: ${error.response.data.message}`);
    }
  };

  return (
    <div className="border p-4 rounded shadow-md">
      <h3 className="text-lg font-bold">{skill.name}</h3>
      <p>{skill.description}</p>
      <p className="text-sm text-gray-500">
        Available Hours: {skill.hoursAvailable}
      </p>
      <p className="text-sm text-gray-500">
        Credit Value: {skill.timeCreditValue}
      </p>
      <div className="flex gap-2 mt-2">
        <button
          onClick={handleEarn}
          className="bg-green-500 text-white px-4 py-1 rounded"
        >
          Earn Credits
        </button>
        <button
          onClick={handleSpend}
          className="bg-red-500 text-white px-4 py-1 rounded"
        >
          Spend Credits
        </button>
      </div>
    </div>
  );
};

export default SkillCard;
