import React, { useState } from "react";
import axios from "axios";

function AddSkill() {
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [hoursAvailable, setHoursAvailable] = useState("");

    const handleAddSkill = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem("token");
        if (!token) {
            alert("You must be logged in to add a skill.");
            return;
        }
        try {
            await axios.post(
                "http://localhost:5000/api/skills/add",
                { name, description, hoursAvailable },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert("✅ Skill added successfully!");
            setName("");
            setDescription("");
            setHoursAvailable("");
        } catch (error) {
            alert("❌ Failed to add skill: " + error.response?.data?.message);
        }
    };

    return (
        <div className="bg-white p-6 shadow-md rounded-lg max-w-lg mx-auto mt-6">
            <h2 className="text-2xl font-bold text-purple-700 mb-4 text-center">➕ Add a New Skill</h2>

            <form onSubmit={handleAddSkill} className="space-y-4">
                {/* Skill Name */}
                <div>
                    <label className="block text-gray-700 font-medium">Skill Name</label>
                    <input
                        type="text"
                        placeholder="Enter skill name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                        className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-purple-400 outline-none"
                    />
                </div>

                {/* Skill Description */}
                <div>
                    <label className="block text-gray-700 font-medium">Description</label>
                    <textarea
                        placeholder="Enter skill description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                        rows="3"
                        className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-purple-400 outline-none"
                    />
                </div>

                {/* Hours Available */}
                <div>
                    <label className="block text-gray-700 font-medium">Available Hours</label>
                    <input
                        type="number"
                        placeholder="Enter available hours"
                        value={hoursAvailable}
                        onChange={(e) => setHoursAvailable(e.target.value)}
                        required
                        className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-purple-400 outline-none"
                    />
                </div>

                {/* Submit Button */}
                <button
                    type="submit"
                    className="w-full bg-purple-600 text-white font-semibold p-3 rounded-lg hover:bg-purple-700 transition"
                >
                    Add Skill
                </button>
            </form>
        </div>
    );
}

export default AddSkill;
