import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FiMenu, FiX } from "react-icons/fi";
import { checkAdminStatus } from "../services/adminService";

const Navbar = ({ isLoggedIn, showSidebar, setShowSidebar }) => {
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const checkAdmin = async () => {
      if (isLoggedIn) {
        const { isAdmin } = await checkAdminStatus();
        setIsAdmin(isAdmin);
      }
    };
    
    checkAdmin();
  }, [isLoggedIn]);

  return (
    <div className="bg-white shadow-md p-4 sticky top-0 z-10 flex justify-between items-center">
      <div className="flex items-center space-x-4">
        {/* Sidebar Toggle Button */}
        <button
          onClick={() => setShowSidebar(!showSidebar)}
          className="text-purple-600 text-2xl focus:outline-none"
        >
          {showSidebar ? <FiX /> : <FiMenu />}
        </button>
        <h2 className="text-2xl font-semibold text-purple-600">Welcome</h2>
      </div>
      <div className="flex space-x-4">
        {!isLoggedIn ? (
          <>
            <button
              onClick={() => navigate("/login")}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
            >
              Login
            </button>
            <button
              onClick={() => navigate("/signup")}
              className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition"
            >
              Sign Up
            </button>
          </>
        ) : (
          <>
            {isAdmin && (
              <button
                onClick={() => navigate("/admin/dashboard")}
                className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
              >
                Admin Panel
              </button>
            )}
            <button
              onClick={() => navigate("/dashboard")}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
            >
              Go to Dashboard
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Navbar;
