import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import { FiLock, FiMail, FiArrowLeft, FiClock } from "react-icons/fi";

const AdminLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if already logged in as admin
    const token = localStorage.getItem("adminToken");
    if (token) {
      navigate("/admin/dashboard");
    }
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      console.log("Attempting admin login...");
      const res = await axios.post("http://localhost:5000/api/auth/login", { email, password });
      
      console.log("Login response:", res.data);
      
      if (res.data.token) {
        // Verify if user is admin
        localStorage.setItem("token", res.data.token); // Store temporarily
        
        try {
          console.log("Checking admin status...");
          const profileRes = await axios.get("http://localhost:5000/api/auth/profile", {
            headers: { Authorization: `Bearer ${res.data.token}` },
          });
          
          console.log("Profile data:", profileRes.data);
          
          if (profileRes.data.isAdmin) {
            if (rememberMe) {
              localStorage.setItem("adminToken", res.data.token);
            } else {
              sessionStorage.setItem("adminToken", res.data.token);
            }
            toast.success("Admin login successful!");
            navigate("/admin/dashboard");
          } else {
            localStorage.removeItem("token");
            toast.error("Access denied. Admin privileges required.");
          }
        } catch (profileError) {
          console.error("Error fetching profile:", profileError);
          localStorage.removeItem("token");
          toast.error("Failed to verify admin status.");
        }
      }
    } catch (error) {
      console.error("Login error:", error);
      toast.error(error.response?.data?.message || "Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-600 to-purple-700 flex flex-col items-center justify-center p-4">
      {/* Logo & Title Section */}
      <div className="mb-8 text-center animate-fade-in-down">
        <div className="bg-white p-4 rounded-full shadow-lg inline-block mb-4">
          <FiClock className="text-5xl text-indigo-600" />
        </div>
        <h1 className="text-3xl md:text-4xl font-bold text-white mb-2">Virtual Time Bank</h1>
        <p className="text-indigo-200">Admin Management Portal</p>
      </div>
      
      {/* Login Card */}
      <div className="bg-white rounded-xl shadow-2xl p-8 md:p-10 max-w-md w-full animate-fade-in-up">
        <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">Administrator Login</h2>
        
        <form onSubmit={handleLogin} className="space-y-6">
          {/* Email Field */}
          <div className="group">
            <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
              Email Address
            </label>
            <div className="relative transition-all duration-300 focus-within:ring-2 focus-within:ring-indigo-500 rounded-md">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none text-gray-500">
                <FiMail />
              </div>
              <input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="admin@example.com"
                required
                className="block w-full pl-10 pr-3 py-3 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 
                focus:outline-none focus:border-indigo-500 transition-colors"
                aria-label="Email Address"
              />
            </div>
          </div>
          
          {/* Password Field */}
          <div className="group">
            <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-1">
              Password
            </label>
            <div className="relative transition-all duration-300 focus-within:ring-2 focus-within:ring-indigo-500 rounded-md">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none text-gray-500">
                <FiLock />
              </div>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="••••••••"
                required
                className="block w-full pl-10 pr-3 py-3 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 
                focus:outline-none focus:border-indigo-500 transition-colors"
                aria-label="Password"
              />
            </div>
          </div>
          
          {/* Remember Me Option */}
          <div className="flex items-center">
            <input
              id="remember-me"
              type="checkbox"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
              className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
            />
            <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-700">
              Remember me
            </label>
          </div>
          
          {/* Submit Button */}
          <button
            type="submit"
            disabled={loading}
            className={`w-full flex justify-center items-center py-3 px-4 border border-transparent rounded-md 
            shadow-md text-white bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700
            focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all
            ${loading ? 'opacity-80 cursor-not-allowed' : 'transform hover:-translate-y-0.5'}`}
          >
            {loading ? (
              <>
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Signing in...
              </>
            ) : "Sign in as Admin"}
          </button>
        </form>
        
        {/* Navigation Links */}
        <div className="mt-8 text-center space-y-3">
          <button 
            onClick={() => navigate("/")}
            className="flex items-center justify-center mx-auto text-indigo-600 hover:text-indigo-800 transition-colors"
          >
            <FiArrowLeft className="mr-2" />
            Return to Homepage
          </button>
          
          <p className="text-sm text-gray-500">
            Need help? Contact <a href="mailto:admin@timebank.com" className="text-indigo-600 hover:underline">admin@timebank.com</a>
          </p>
        </div>
      </div>
      
      {/* Footer */}
      <div className="mt-8 text-center text-indigo-200 text-sm">
        © {new Date().getFullYear()} Virtual Time Bank. All rights reserved.
      </div>
      
      {/* Add animation styles */}
      <style jsx>{`
        @keyframes fadeInDown {
          from { opacity: 0; transform: translateY(-20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        @keyframes fadeInUp {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fade-in-down {
          animation: fadeInDown 0.6s ease-out forwards;
        }
        .animate-fade-in-up {
          animation: fadeInUp 0.6s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

export default AdminLogin;
