import React from "react";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();



  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <div className="h-screen bg-gradient-to-r from-blue-500 to-purple-600 text-white">
      {/* Navbar */}
      <nav className="flex justify-between items-center px-6 py-4 bg-white shadow-md">
        <div className="flex items-center space-x-2">
          <img src="https://via.placeholder.com/40" alt="Logo" className="w-10 h-10" />
          <h1 className="text-xl font-bold text-blue-600">Time Bank</h1>
        </div>
        <div className="space-x-4">
          <button  onClick={() => navigate("/login")} className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition">Login</button>
          <button onClick={() => navigate("/signup")} className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition">Sign Up</button>
        </div>
      </nav>

      {/* Home Page Content */}
      <div className="flex flex-col items-center justify-center h-[calc(100vh-64px)] text-center px-6">
        <h2 className="text-4xl font-bold mb-4 animate-fade-in">Welcome to the Virtual Time Bank</h2>
        <p className="text-lg mb-6 max-w-lg">Exchange your skills and earn time credits! Join our community and start saving time today.</p>
        <button className="px-6 py-3 bg-white text-blue-600 font-semibold rounded-full shadow-md hover:bg-blue-100 transition">
          Get Started

        </button>

        {/* Show "Back to Dashboard" if logged in */}
      {isLoggedIn && (
        <button
          onClick={() => navigate("/dashboard")}
          className="bg-white text-purple-600 font-semibold px-6 py-3 rounded-lg shadow-md hover:bg-gray-200 transition"
        >
          Back to Dashboard
        </button>
      )}
      </div>
    </div>
  );
}

export default Home;
