import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FiUser, FiEdit, FiSave, FiArrowLeft, FiLayers, FiLogOut, FiPlus, FiTrash } from "react-icons/fi";

function Profile() {
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [updatedUser, setUpdatedUser] = useState({ name: "", skills: [] });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    } else {
      fetchUserProfile(token);
    }
  }, []);

  const fetchUserProfile = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/auth/profile", {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      if (response.ok) {
        setUser(data);
        setUpdatedUser({ name: data.name, skills: data.skills || [] });
      } else {
        alert("Session expired! Please log in again.");
        localStorage.removeItem("token");
        navigate("/login");
      }
    } catch (error) {
      console.error("Error fetching user profile:", error);
    }
  };

  const handleUpdateProfile = async () => {
    const token = localStorage.getItem("token");
    try {
      const response = await fetch("http://localhost:5000/api/auth/update-profile", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(updatedUser),
      });
      if (response.ok) {
        alert("Profile updated successfully!");
        setEditMode(false);
        fetchUserProfile(token);
      } else {
        alert("Failed to update profile.");
      }
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleSkillChange = (index, key, value) => {
    const newSkills = [...updatedUser.skills];
    newSkills[index][key] = value;
    setUpdatedUser({ ...updatedUser, skills: newSkills });
  };

  const addSkill = () => {
    setUpdatedUser({
      ...updatedUser,
      skills: [...updatedUser.skills, { name: "", hoursAvailable: "" }],
    });
  };

  const removeSkill = (index) => {
    const newSkills = updatedUser.skills.filter((_, i) => i !== index);
    setUpdatedUser({ ...updatedUser, skills: newSkills });
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <div className="w-64 bg-white shadow-lg p-5 flex flex-col">
        <h2 className="text-2xl font-bold text-purple-700 mb-6 flex items-center">
          <FiLayers className="mr-2" /> Virtual Time Bank
        </h2>
        <nav className="flex flex-col space-y-4">
          <button onClick={() => navigate("/dashboard")} className="flex items-center p-3 rounded-lg hover:bg-purple-100">
            <FiArrowLeft className="mr-3" /> Back to Dashboard
          </button>
          <button onClick={handleLogout} className="flex items-center p-3 rounded-lg text-red-500 hover:bg-red-100">
            <FiLogOut className="mr-3" /> Logout
          </button>
        </nav>
      </div>

      <div className="flex-1 flex flex-col p-6">
        <h2 className="text-2xl font-semibold text-purple-700 mb-4">Profile</h2>
        {user ? (
          <div className="bg-white shadow-lg p-6 rounded-lg">
            <div className="flex items-center space-x-4">
              <FiUser className="text-4xl text-purple-600" />
              {editMode ? (
                <input
                  type="text"
                  value={updatedUser.name}
                  onChange={(e) => setUpdatedUser({ ...updatedUser, name: e.target.value })}
                  className="border p-2 rounded-md"
                />
              ) : (
                <h3 className="text-xl font-semibold">{user.name}</h3>
              )}
            </div>
            <p className="text-gray-600">Email: {user.email}</p>
            <div className="mt-4">
              <h4 className="text-lg font-bold text-purple-700">Skills</h4>
              {editMode ? (
                <div>
                  {updatedUser.skills.map((skill, index) => (
                    <div key={index} className="flex items-center space-x-2 mt-2">
                      <input
                        type="text"
                        placeholder="Skill Name"
                        value={skill.name}
                        onChange={(e) => handleSkillChange(index, "name", e.target.value)}
                        className="border p-2 rounded-md"
                      />
                      <input
                        type="number"
                        placeholder="Hours"
                        value={skill.hoursAvailable}
                        onChange={(e) => handleSkillChange(index, "hoursAvailable", e.target.value)}
                        className="border p-2 rounded-md w-20"
                      />
                      <button onClick={() => removeSkill(index)} className="text-red-500">
                        <FiTrash />
                      </button>
                    </div>
                  ))}
                  <button onClick={addSkill} className="mt-2 px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-700">
                    <FiPlus className="inline mr-2" /> Add Skill
                  </button>
                </div>
              ) : (
                <ul className="list-disc ml-6">
                  {user.skills && user.skills.length > 0 ? (
                    user.skills.map((skill, index) => (
                      <li key={index} className="text-gray-600">{skill.name} ({skill.hoursAvailable} hours)</li>
                    ))
                  ) : (
                    <p className="text-gray-500">No skills added yet.</p>
                  )}
                </ul>
              )}
            </div>
            {editMode ? (
              <button onClick={handleUpdateProfile} className="mt-4 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700">
                <FiSave className="inline mr-2" /> Save
              </button>
            ) : (
              <button onClick={() => setEditMode(true)} className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
                <FiEdit className="inline mr-2" /> Edit Profile
              </button>
            )}
          </div>
        ) : (
          <p className="text-center text-gray-500">Loading...</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
