import axios from "axios";

const API_URL = "http://localhost:5000/api/admin";

// Get auth token
const getAuthToken = () => {
  return localStorage.getItem("adminToken") || localStorage.getItem("token");
};

// Get dashboard stats
export const getAdminStats = async () => {
  const res = await axios.get(`${API_URL}/stats`, {
    headers: { Authorization: `Bearer ${getAuthToken()}` },
  });
  return res.data;
};

// User management
export const getAllUsers = async () => {
  const res = await axios.get(`${API_URL}/users`, {
    headers: { Authorization: `Bearer ${getAuthToken()}` },
  });
  return res.data;
};

export const getUserById = async (id) => {
  const res = await axios.get(`${API_URL}/users/${id}`, {
    headers: { Authorization: `Bearer ${getAuthToken()}` },
  });
  return res.data;
};

export const updateUser = async (id, userData) => {
  const res = await axios.put(`${API_URL}/users/${id}`, userData, {
    headers: { Authorization: `Bearer ${getAuthToken()}` },
  });
  return res.data;
};

export const deleteUser = async (id) => {
  const res = await axios.delete(`${API_URL}/users/${id}`, {
    headers: { Authorization: `Bearer ${getAuthToken()}` },
  });
  return res.data;
};

// Skills management
export const getAllSkills = async () => {
  const res = await axios.get(`${API_URL}/skills`, {
    headers: { Authorization: `Bearer ${getAuthToken()}` },
  });
  return res.data;
};

export const updateSkill = async (id, skillData) => {
  const res = await axios.put(`${API_URL}/skills/${id}`, skillData, {
    headers: { Authorization: `Bearer ${getAuthToken()}` },
  });
  return res.data;
};

export const deleteSkill = async (id) => {
  const res = await axios.delete(`${API_URL}/skills/${id}`, {
    headers: { Authorization: `Bearer ${getAuthToken()}` },
  });
  return res.data;
};

// Transactions
export const getAllTransactions = async () => {
  const res = await axios.get(`${API_URL}/transactions`, {
    headers: { Authorization: `Bearer ${getAuthToken()}` },
  });
  return res.data;
};

// Check if user is admin
export const checkAdminStatus = async () => {
  const token = getAuthToken();
  if (!token) return { isAdmin: false };
  
  try {
    const res = await axios.get("http://localhost:5000/api/auth/is-admin", {
      headers: { Authorization: `Bearer ${token}` }
    });
    return res.data;
  } catch (error) {
    return { isAdmin: false };
  }
};
