import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { FiLogIn, FiUserPlus, FiHome, FiLayers, FiLogOut, FiCompass } from "react-icons/fi";

function Home() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [skills, setSkills] = useState([]);
  const navigate = useNavigate();
  const skillsRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
    }
    fetchSkills();
  }, []);

  const fetchSkills = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/skills");
      const data = await response.json();
      if (response.ok) {
        setSkills(data);
      } else {
        console.error("Failed to fetch skills");
      }
    } catch (error) {
      console.error("Error fetching skills:", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const scrollToSkills = () => {
    skillsRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Navbar */}
      <div className="bg-white shadow p-4 flex justify-between items-center">
        <h2 className="text-2xl font-semibold text-purple-600">Welcome</h2>
        <div className="flex space-x-4">
          {!isLoggedIn ? (
            <>
              <button onClick={() => navigate("/login")} className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
                Login
              </button>
              <button onClick={() => navigate("/signup")} className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700">
                Sign Up
              </button>
            </>
          ) : (
            <button onClick={() => navigate("/dashboard")} className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
              Go to Dashboard
            </button>
          )}
        </div>
      </div>

      {/* Hero Section */}
      <div className="flex flex-col items-center justify-center h-[calc(100vh-64px)] text-center px-6">
        <h2 className="text-4xl font-bold mb-4">Welcome to the Virtual Time Bank</h2>
        <p className="text-lg mb-6 max-w-lg">Exchange your skills and earn time credits! Join our community and start saving time today.</p>
        <button onClick={scrollToSkills} className="px-6 py-3 bg-blue-600 text-white font-semibold rounded-full shadow-md hover:bg-blue-700">
          Get Started
        </button>
      </div>

      {/* Available Skills Section */}
      <div ref={skillsRef} className="p-6">
        <h3 className="text-2xl font-bold text-purple-700 mb-4">Available Skills</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {skills.length > 0 ? (
            skills.map((skill) => (
              <div key={skill._id} className="bg-white shadow-lg p-4 rounded-lg cursor-pointer hover:shadow-xl" onClick={() => navigate(`/skill/${skill._id}`)}>
                <h4 className="text-xl font-semibold text-blue-600">{skill.name}</h4>
                <p className="text-gray-600">{skill.description}</p>
                <p className="text-sm text-gray-500">{skill.hoursAvailable} hours available</p>
              </div>
            ))
          ) : (
            <p className="text-gray-500">No skills available yet.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
